    },
    product_sml_icon_2:{
        height: 30,
        width: 30,
        maxHeight: 30,
        maxWidth: 30,
    },
    stretch: {
        width: 25,
        height: 25,
        textAlign: 'center',
        justifyContent: 'center',
        alignItems: 'center',
    },
    footer: {
        flex: 1,
        backgroundColor: 'rgb(235 235 235)',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30
    },
    text_header: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 30
    },
    text_footer: {
        color: '#05375a',
        fontSize: 18
    },
    action: {
        marginTop: normalize(5),
        maxHeight: 50,
        flex:1,
        flexDirection:'row',
        alignItems:'center',
        // justifyContent:'center',
        borderRadius: normalize(10),
        borderWidth: 1,
        borderColor: "black",
        paddingLeft: 3,
        paddingRight: 3,
    },
    actionError: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomColor: '#FF0000',
        paddingBottom: 5
    },
    textInput: {
        height: normalize(20),
        flex: 1,
        paddingLeft: 10,
        color: '#05375a',
    },
    errorMsg: {
        color: '#FF0000',
        fontSize: 14,
    },
    button: {
        alignItems: 'center',
        marginTop: normalize(30),
    },
    survey_button:{
        width: '100%',
        minHeight: 30,
        maxHeight: 40,
        justifyContent: 'center',
        height: 150,
        alignItems: 'center',
        borderRadius: 20,
        padding: normalize(8),
        backgroundColor: '#4cb050',
        marginBottom: 5,
    },
    textSign: {
        fontSize: normalize(14),
        fontWeight: "500",
        paddingLeft: 10,
        paddingRight: 10
    },
    center :{
        justifyContent: 'center', //Centered vertically
        alignItems: 'center', // Centered horizontally
        flex:1, 
    },
    points: {
        display:'flex', 
        flexDirection:'row', 
        justifyContent: 'space-evenly', 
        backgroundColor: '#1a1a1a', 
        alignContent: "center",
        // height: 'justifyContent',
        paddingLeft: normalize(5),
        paddingRight: normalize(5),
        borderRadius: 20,
    },
    items:{
        height: 190,
        display:'flex', 
        flexDirection:'row', 
        justifyContent: 'space-evenly', 
        backgroundColor: '#fff', 
        alignContent: "center",
        paddingLeft: normalize(5),
        paddingRight: normalize(5),
        borderRadius: 10,
        marginTop: normalize(10),
        borderColor: 'black',
        borderRadius: 10,
        padding: 10,
        shadowColor: '#000000',
        shadowOffset: {
        width: 0,
        height: 1
        },
        shadowRadius: 2,
        shadowOpacity: 1.0
    },
    products: {
        paddingTop: 4,
        paddingBottom: 4,
        width: SCREEN_WIDTH*0.94,
        alignSelf: "center",
        backgroundColor: '#fff',
        minHeight:140,
        display:'flex',
        flexDirection:'row', 
        justifyContent: "space-evenly",
        borderRadius: 10,
        marginTop: normalize(10),
        borderColor: 'black',
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 0},
        shadowOpacity: 0.8,
        shadowRadius: 2,  
        elevation: 5,
